document.addEventListener("DOMContentLoaded",(event=>{const menuBtn=document.getElementById("menu-btn");const mobileMenu=document.getElementById("mobile-menu");menuBtn.addEventListener("click",(()=>{mobileMenu.classList.toggle("hidden")}));const navbarLinks=document.querySelectorAll("nav a, #mobile-menu a");navbarLinks.forEach((link=>{link.addEventListener("click",(event=>{const href=link.getAttribute("href");if(href==="#"){event.preventDefault();window.scrollTo({top:0,behavior:"smooth"})}else{event.preventDefault();const targetId=href.substring(1);const targetElement=document.getElementById(targetId);if(targetElement){const offset=100;const elementPosition=targetElement.getBoundingClientRect().top;const offsetPosition=elementPosition+window.scrollY-offset;window.scrollTo({top:offsetPosition,behavior:"smooth"});if(!mobileMenu.classList.contains("hidden")){mobileMenu.classList.add("hidden")}}}}))}))}));